import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [renderAddExpenseForm, setRenderAddExpenseForm] = useState(false);

  // const [userInput, setUserInput] = useState({
  //     enteredTitle: '',
  //     enteredAmount: '',
  //     enteredDate: ''
  // })

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    // setUserInput({
    //     ...userInput,
    //     enteredTitle: event.target.value,
    // })
    // alternative for above
    // setUserInput((prevState) => {
    //     return {...prevState, enteredTitle: event.target.value}
    // })
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    //   setUserInput({
    //       ...userInput,
    //       enteredAmount: event.target.value,
    //   })
    // alternative for above
    // setUserInput((prevState) => {
    //     return {...prevState, enteredAmount: event.target.value}
    // })
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    //   setUserInput({
    //       ...userInput,
    //       enteredDate: event.target.value,
    //   })
    // alternative for above
    // setUserInput((prevState) => {
    //     return { ...prevState, enteredDate: event.target.value}
    // })
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");

    props.onSaveExpenseData(expenseData);

    setRenderAddExpenseForm(false);
  };

  const onSubmitAddNewExpenseClickHandler = (event) => {
    event.preventDefault();
    setRenderAddExpenseForm(true);
  };

  const onClickCancelHander = (event) => {
    event.preventDefault();
    setRenderAddExpenseForm(false);
  };

  if (!Boolean(renderAddExpenseForm)) {
    return (
        <div className="new-expense__actions">
          <button type="submit" onClick={onSubmitAddNewExpenseClickHandler}>Add New Expense</button>
        </div>
    );
  }

  return (
    <div>
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              onChange={titleChangeHandler}
              value={enteredTitle}
            />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              type="number"
              onChange={amountChangeHandler}
              min="0.01"
              step="0.01"
              value={enteredAmount}
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              type="date"
              onChange={dateChangeHandler}
              min="2021-01-01"
              max="2022-12-31"
              value={enteredDate}
            />
          </div>
          <div className="new-expense__actions">
            <button type="submit" onClick={onClickCancelHander}>
              Cancel
            </button>
          </div>
          <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ExpenseForm;
