import React, { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import "./components/Expenses/Expenses.css";
import NewExpense from "./components/NewExpense/NewExpense";


const dummy_expenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(dummy_expenses);

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };


  const getSelectedYearHandler = (selectedYear) => {
    console.log(selectedYear);
    expenses.map((expense) => {
      if(expense.date.getFullYear().toString() === selectedYear.toString()){
        alert(expense.title);
        filteredExpenses.push(expense);
      }
   })
   alert('Filtered Array Size ::: '+filteredExpenses.length);
  };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <div className="expenses">
        <Expenses items={expenses} getSelectedYear={getSelectedYearHandler}/>
      </div>
    </div>
  );
};

export default App;
